import React, { Component } from "react";

import "./App.css";

import AnimalCard from "./Animal-Card";
import { animals } from "./animals-array";
import { render } from "react-dom";

class App extends Component {
  state = {
    animals: animals,
  };

  removeHandler = (name) => {
    const updatedArray = this.state.animals.filter(animal.name !== name);
    this.setState({ animals: updatedArray });
  };

  render() {
    return (
      <div className="animalsList">
        {this.state.animals.map((animal) => (
          <AnimalCard
            key={animal.name}
            name={animal.name}
            remove={this.removeHandler.bind(this, animal.name)}
            // remove={() => this.removeHandler(animal.name)}
          />
        ))}
      </div>
    );
  }
}

export default App;
