//import "./App.css";
import Header from "./Header.js";
import Footer from "./Footer.js";
import Box from "./box.js";
//const name = "Pravesh Chand";
//const time = 2020;

const persons = [
  {
    name: "Pravesh",
    title: "CEO",
    age: "33",
    language: [
      { name: "english", id: 1 },
      { name: "hindi", id: 2 },
      { name: "tibetian", id: 3 },
    ],
  },
  {
    name: "Pravesh",
    title: "CEO",
    age: "33",
    language: [
      { name: "english", id: 1 },
      { name: "hindi", id: 2 },
    ],
  },
  {
    name: "Pravesh",
    title: "CEO",
    age: "33",
    language: [
      { name: "english", id: 1 },
      { name: "hindi", id: 2 },
      { name: "tibetian", id: 3 },
    ],
  },
];

// Things to remember in construction, [] bracket are must , data is written in curly braces {}, semi-colon is used to for information seperation with comma. See above.

function App() {
  return (
    <div className="App">
      <Header />

      <main>
        {persons.map((person) => (
          <Box
            key={person.name}
            name={person.name}
            title={person.title}
            age={person.age}
            lang={person.languages.map((lang) => (
              <li key={lang.id}>{lang.name}</li>
            ))}
          />
        ))}
      </main>

      <Footer />
    </div>
  );
}

export default App;
