  return (
    <main>
      <div>
        <h1>Animals</h1>
      </div>
      <div className="container">
        {animals.map((animal) => (
          <AnimalCard
            key={animal.name}
            name={animal.name}
            like={animal.likes}
            /*addLike={() => addLike()}
